import React from 'react';
import ReactDOM from 'react-dom/client';
import { ThemeProvider } from '@mui/material/styles';
import { CssBaseline } from '@mui/material';
import App from './App';
import theme from './theme';
import ErrorBoundary from './components/ErrorBoundary';
import './index.css';

// Disable React error overlay completely
if (process.env.NODE_ENV === 'development') {
  // Override the error overlay
  const originalError = console.error;
  console.error = (...args) => {
    // Log errors but prevent React overlay from showing
    originalError(...args);
  };
  
  // Disable React error overlay by overriding the handler
  if (window.__REACT_ERROR_OVERLAY_GLOBAL_HANDLER__) {
    window.__REACT_ERROR_OVERLAY_GLOBAL_HANDLER__ = function() {
      // Do nothing - prevent React error overlay
    };
  }
  
  // Override handleError if it exists
  if (window.handleError) {
    window.handleError = function(error) {
      console.error('Error caught:', error?.message || error?.toString() || 'Unknown error');
      // Don't show React overlay
    };
  }
}

// Global error handler for unhandled errors
window.addEventListener('error', (event) => {
  console.error('Global error:', event.error);
  
  // Format error properly
  let errorMessage = 'An error occurred';
  if (event.error) {
    if (typeof event.error === 'string') {
      errorMessage = event.error;
    } else if (event.error.message) {
      errorMessage = event.error.message;
    } else if (event.error.toString && typeof event.error.toString === 'function') {
      errorMessage = event.error.toString();
    }
  }
  
  console.error('Error message:', errorMessage);
  
  // Prevent default error handling to avoid "[object Object]"
  if (event.error && typeof event.error === 'object') {
    event.preventDefault();
  }
}, true);

// Global handler for unhandled promise rejections
window.addEventListener('unhandledrejection', (event) => {
  // Filter out browser extension errors (wallet, etc.)
  if (event.reason) {
    const reason = event.reason;
    const reasonStr = typeof reason === 'string' ? reason : 
                     (reason?.message || reason?.toString?.() || JSON.stringify(reason));
    
    // Ignore browser extension errors
    if (reasonStr.includes('wallet') || 
        reasonStr.includes('MetaMask') || 
        reasonStr.includes('code: 4001') ||
        reasonStr.includes('account')) {
      console.warn('Ignored browser extension error:', reasonStr);
      event.preventDefault();
      return;
    }
  }
  
  console.error('Unhandled promise rejection:', event.reason);
  
  // Format rejection reason properly
  let errorMessage = 'A promise was rejected';
  if (event.reason) {
    if (typeof event.reason === 'string') {
      errorMessage = event.reason;
    } else if (event.reason.message) {
      errorMessage = event.reason.message;
    } else if (event.reason.toString && typeof event.reason.toString === 'function') {
      errorMessage = event.reason.toString();
    } else {
      try {
        errorMessage = JSON.stringify(event.reason);
      } catch (e) {
        errorMessage = 'Unknown error';
      }
    }
  }
  
  console.error('Rejection reason:', errorMessage);
  
  // Prevent default error handling to avoid "[object Object]"
  event.preventDefault();
}, true);

// Wrap render in try-catch to catch any render errors
const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error('Root element not found');
}

const root = ReactDOM.createRoot(rootElement);

try {
  root.render(
    <React.StrictMode>
      <ErrorBoundary>
        <ThemeProvider theme={theme}>
          <CssBaseline />
          <App />
        </ThemeProvider>
      </ErrorBoundary>
    </React.StrictMode>
  );
} catch (error) {
  console.error('Error during render:', error);
  // Fallback render with error message
  root.render(
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Application Error</h1>
      <p>{error?.message || 'An error occurred while loading the application'}</p>
      <button onClick={() => window.location.reload()}>Reload Page</button>
    </div>
  );
}
