import React from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  FormControlLabel, 
  Checkbox, 
  Button, 
  Stack,
  Grid,
  Divider,
  InputAdornment
} from '@mui/material';
import { HealthAndSafety, LocalHospital, RestaurantMenu, CalendarToday } from '@mui/icons-material';
import { useRecipeContext } from '../context/RecipeContext';

const HealthProfile = () => {
  const { healthProfile, updateHealthProfile, getRecommendations } = useRecipeContext();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    
    updateHealthProfile({
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const calculateAge = (birthDate) => {
    if (!birthDate) return null;
    const today = new Date();
    const birth = new Date(birthDate);
    let age = today.getFullYear() - birth.getFullYear();
    const monthDiff = today.getMonth() - birth.getMonth();
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birth.getDate())) {
      age--;
    }
    return age;
  };

  const age = calculateAge(healthProfile.birthDate);

  return (
    <Box 
      sx={{ 
        mb: 4, 
        p: { xs: 3, md: 4 }, 
        borderRadius: 2,
        backgroundColor: 'background.paper',
        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
        border: '1px solid rgba(0, 0, 0, 0.08)',
      }}
    >
      <Stack direction="row" alignItems="center" spacing={2} sx={{ mb: 3 }}>
        <Box
          sx={{
            p: 1.25,
            borderRadius: '10px',
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <HealthAndSafety sx={{ fontSize: 24, color: 'white' }} />
        </Box>
        <Typography 
          variant="h6" 
          sx={{ 
            fontWeight: 700,
            color: 'text.primary',
          }}
        >
          Health Profile
        </Typography>
      </Stack>
      
      <Divider sx={{ mb: 3 }} />
      
      <Grid container spacing={3}>
        <Grid item xs={12} sm={6}>
          <TextField
            fullWidth
            label="Daily Sodium Limit (mg)"
            name="dailySodiumMax"
            type="number"
            value={healthProfile.dailySodiumMax}
            onChange={handleChange}
            InputProps={{ inputProps: { min: 500, max: 3000 } }}
            helperText="Recommended: 1500-2300 mg per day"
          />
        </Grid>
        
        <Grid item xs={12} sm={6}>
          <TextField
            fullWidth
            label="Date of Birth"
            name="birthDate"
            type="date"
            value={healthProfile.birthDate}
            onChange={handleChange}
            InputLabelProps={{
              shrink: true,
            }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <CalendarToday sx={{ color: 'text.secondary', fontSize: 20 }} />
                </InputAdornment>
              ),
            }}
            helperText={age !== null ? `Age: ${age} years` : 'Required for age-appropriate recommendations'}
          />
        </Grid>
        
        <Grid item xs={12}>
          <Typography 
            variant="subtitle2" 
            sx={{ 
              mb: 2,
              fontWeight: 600,
              color: 'text.secondary',
              textTransform: 'uppercase',
              letterSpacing: '0.5px',
              fontSize: '0.75rem',
            }}
          >
            Medical Conditions
          </Typography>
          
          <Box 
            sx={{ 
              display: 'flex',
              flexDirection: 'column',
              gap: 1.5,
            }}
          >
            <FormControlLabel
              control={
                <Checkbox
                  name="hasDiabetes"
                  checked={healthProfile.hasDiabetes}
                  onChange={handleChange}
                  sx={{ 
                    '&.Mui-checked': {
                      color: 'primary.main',
                    }
                  }}
                />
              }
              label={
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <LocalHospital sx={{ fontSize: 18, color: 'text.secondary' }} />
                  <Typography sx={{ fontWeight: 500, fontSize: '0.95rem' }}>
                    I have diabetes
                  </Typography>
                </Box>
              }
            />
            
            <FormControlLabel
              control={
                <Checkbox
                  name="hasRenalDisease"
                  checked={healthProfile.hasRenalDisease}
                  onChange={handleChange}
                  sx={{ 
                    '&.Mui-checked': {
                      color: 'primary.main',
                    }
                  }}
                />
              }
              label={
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <HealthAndSafety sx={{ fontSize: 18, color: 'text.secondary' }} />
                  <Typography sx={{ fontWeight: 500, fontSize: '0.95rem' }}>
                    I have kidney disease
                  </Typography>
                </Box>
              }
            />
          </Box>
        </Grid>
      </Grid>
      
      <Box sx={{ mt: 4, pt: 3, borderTop: '1px solid rgba(0, 0, 0, 0.08)' }}>
        <Button 
          variant="contained" 
          color="primary"
          onClick={getRecommendations}
          fullWidth
          size="large"
          startIcon={<RestaurantMenu />}
          sx={{ 
            py: 1.5,
            fontSize: '1rem',
            fontWeight: 600,
            textTransform: 'none',
          }}
        >
          Get Recommendations
        </Button>
      </Box>
    </Box>
  );
};

export default HealthProfile;
