import React from 'react';
import { Box, Typography, TextField, FormControlLabel, Checkbox, Button } from '@mui/material';
import { useRecipeContext } from '../context/RecipeContext';

const HealthProfile = () => {
  const { healthProfile, updateHealthProfile, getRecommendations } = useRecipeContext();

  const handleChange = (e) => {
  const { name, value, type, checked } = e.target;
  
  updateHealthProfile({
    [name]: type === 'checkbox' ? checked : value
  });
};

  return (
    <Box sx={{ mb: 4, p: 3, border: '1px solid #ccc', borderRadius: 2 }}>
      <Typography variant="h5" gutterBottom>Health Profile</Typography>
      
      <TextField
  fullWidth
  label="Daily Sodium Limit (mg)"
  name="dailySodiumMax"
  type="number"
  value={healthProfile.dailySodiumMax}
  onChange={handleChange}
  margin="normal"
  InputProps={{ inputProps: { min: 500, max: 3000 } }}
/>
      
      <FormControlLabel
        control={
          <Checkbox
            name="hasDiabetes"
            checked={healthProfile.hasDiabetes}
            onChange={handleChange}
          />
        }
        label="I have diabetes"
        sx={{ display: 'block', mt: 1 }}
      />
      
      <FormControlLabel
        control={
          <Checkbox
            name="hasRenalDisease"
            checked={healthProfile.hasRenalDisease}
            onChange={handleChange}
          />
        }
        label="I have kidney disease"
        sx={{ display: 'block', mb: 2 }}
      />
      
      <Button 
        variant="contained" 
        color="primary"
        onClick={getRecommendations}
      >
        Get Recommendations
      </Button>
    </Box>
  );
};

export default HealthProfile;
