import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
    background: {
      paper: 'rgba(255, 255, 255, 0.9)', // Semi-transparent white for cards
      default: 'transparent', // Transparent background for the app
    },
  },
  components: {
    // Override Paper component (used by most MUI components)
    MuiPaper: {
      styleOverrides: {
        root: {
          backgroundColor: 'rgba(255, 255, 255, 0.9)',
          backdropFilter: 'blur(10px)',
          borderRadius: '15px',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
        },
      },
    },
    // Override Container
    MuiContainer: {
      styleOverrides: {
        root: {
          backgroundColor: 'transparent',
        },
      },
    },
    // Override Typography for better readability
    MuiTypography: {
      styleOverrides: {
        root: {
          textShadow: '1px 1px 2px rgba(0, 0, 0, 0.1)',
        },
      },
    },
  },
});

export default theme;
