import React from 'react';
import { 
  Card, 
  CardContent, 
  Typography, 
  Chip, 
  Stack, 
  Box, 
  Divider 
} from '@mui/material';
import { Favorite, AccessTime, LocalDining } from '@mui/icons-material';

const RecipeCard = ({ recipe }) => {
  const getSodiumStatus = (sodium) => {
    if (sodium < 150) return { label: 'Very Low Sodium', color: 'success' };
    if (sodium < 300) return { label: 'Low Sodium', color: 'success' };
    if (sodium < 600) return { label: 'Moderate Sodium', color: 'warning' };
    return { label: 'High Sodium', color: 'error' };
  };

  const sodiumStatus = getSodiumStatus(recipe.sodium);

  return (
    <Card 
      sx={{ 
        height: '100%', 
        display: 'flex', 
        flexDirection: 'column',
        backgroundColor: 'background.paper',
        transition: 'all 0.3s ease-in-out',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: '0 12px 40px rgba(0, 0, 0, 0.15)',
        }
      }}
    >
      <CardContent sx={{ flexGrow: 1, p: 3 }}>
        <Typography 
          variant="h6" 
          gutterBottom
          sx={{
            fontWeight: 700,
            mb: 2,
            color: 'text.primary',
            fontSize: '1.25rem',
          }}
        >
          {recipe.name}
        </Typography>
        
        <Stack 
          direction="row" 
          spacing={1} 
          sx={{ 
            mb: 2.5,
            flexWrap: 'wrap',
            gap: 1,
          }}
        >
          <Chip 
            label={`${recipe.matchScore}% Match`}
            color="primary"
            size="small"
            sx={{ fontWeight: 600 }}
          />
          <Chip 
            label={sodiumStatus.label}
            color={sodiumStatus.color}
            size="small"
            sx={{ fontWeight: 500 }}
          />
          {recipe.hasDiabetes && recipe.carbs > 30 && (
            <Chip 
              label="High Carb"
              color="warning"
              size="small"
              sx={{ fontWeight: 500 }}
            />
          )}
        </Stack>
        
        <Typography 
          variant="body2" 
          color="text.secondary" 
          gutterBottom
          sx={{ 
            mb: 2,
            lineHeight: 1.6,
            fontSize: '0.9rem',
          }}
        >
          <Box component="span" sx={{ fontWeight: 600, color: 'text.primary' }}>
            Ingredients:
          </Box>{' '}
          {recipe.ingredients.map(i => i.name).join(', ')}
        </Typography>
        
        <Divider sx={{ my: 2.5, opacity: 0.3 }} />
        
        <Box 
          sx={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            mb: 2,
            flexWrap: 'wrap',
            gap: 1,
          }}
        >
          <Chip 
            icon={<AccessTime fontSize="small" />}
            label={`${Math.floor(Math.random() * 10) + 15} min`}
            size="small"
            sx={{ fontWeight: 500 }}
          />
          <Chip 
            icon={<LocalDining fontSize="small" />}
            label={`${recipe.calories} cal`}
            size="small"
            sx={{ fontWeight: 500 }}
          />
          <Chip 
            icon={<Favorite fontSize="small" />}
            label={`${recipe.sodium}mg sodium`}
            size="small"
            color={sodiumStatus.color}
            sx={{ fontWeight: 500 }}
          />
        </Box>
        
        <Typography 
          variant="body2" 
          sx={{ 
            mt: 2,
            fontStyle: 'italic',
            color: 'text.secondary',
            lineHeight: 1.6,
            fontSize: '0.85rem',
          }}
        >
          {recipe.instructions.substring(0, 120)}...
        </Typography>
      </CardContent>
    </Card>
  );
};

export default RecipeCard;
