import React, { useEffect } from 'react';
import { Box, Typography, CircularProgress, Alert, Paper } from '@mui/material';
import { useRecipeContext } from '../context/RecipeContext';
import RecipeCard from './RecipeCard';

const RecommendationList = () => {
  const { recommendations, loading, error } = useRecipeContext(); // Add error state
  
  // Add debug logging
  useEffect(() => {
    console.log("Recommendations:", recommendations);
    console.log("Loading state:", loading);
    if (error) console.error("Error:", error);
  }, [recommendations, loading, error]);

  return (
    <Box sx={{ mb: 4 }}>
      <Typography variant="h5" gutterBottom>Recommended Recipes</Typography>
      
      {error ? (
        <Alert severity="error">{error}</Alert>
      ) : loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
          <CircularProgress />
          <Typography variant="body1" sx={{ ml: 2 }}>Loading recommendations...</Typography>
        </Box>
      ) : recommendations.length === 0 ? (
        <Paper elevation={0} sx={{ p: 3, textAlign: 'center' }}>
          <Typography variant="body1" color="textSecondary">
            Add ingredients and set your health profile to get recommendations
          </Typography>
        </Paper>
      ) : (
        <Box sx={{ display: 'grid', gridTemplateColumns: { xs: '1fr', sm: '1fr 1fr' }, gap: 3 }}>
          {recommendations.map(recipe => (
            <RecipeCard key={recipe.id} recipe={recipe} />
          ))}
        </Box>
      )}
    </Box>
  );
};

export default RecommendationList;
