// Add error state to context
export function RecipeProvider({ children }) {
  const [ingredients, setIngredients] = useState([]);
  const [healthProfile, setHealthProfile] = useState({
    dailySodiumMax: 1500,
    hasDiabetes: false,
    hasRenalDisease: false
  });
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null); // Add error state

  // ... other functions ...

  const getRecommendations = async () => {
    setLoading(true);
    setError(null); // Reset error
    try {
      console.log("Sending request with:", { ingredients, healthProfile });
      const data = await recommendRecipes(ingredients, healthProfile);
      console.log("Received data:", data);
      setRecommendations(data);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
      setError('Failed to load recommendations. Please try again.'); // Set error message
    } finally {
      setLoading(false);
    }
  };

  return (
    <RecipeContext.Provider
      value={{
        ingredients,
        healthProfile,
        recommendations,
        loading,
        error, // Include error in context
        // ... other values ...
      }}
    >
      {children}
    </RecipeContext.Provider>
  );
}
