import React, { useState } from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  Chip, 
  Grid,
  Autocomplete 
} from '@mui/material';
import { useRecipeContext } from '../context/RecipeContext';

const commonIngredients = [
  'chicken', 'salmon', 'rice', 'pasta', 'tomato', 
  'onion', 'garlic', 'broccoli', 'carrot', 'lettuce',
  'olive oil', 'lemon', 'soy sauce', 'lentils', 'beans',
  'potato', 'spinach', 'egg', 'milk', 'cheese'
];

const IngredientSelector = () => {
  const { ingredients, addIngredient, removeIngredient } = useRecipeContext();
  const [inputValue, setInputValue] = useState('');
  
  const handleAdd = () => {
  if (inputValue.trim()) {
    const normalized = normalizeIngredient(inputValue);
    addIngredient(normalized);
    setInputValue('');
  }
};
  
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleAdd();
    }
  };

const normalizeIngredient = (ing) => {
  return ing.trim().toLowerCase().replace(/s+/g, ' ');
};

  return (
    <Box sx={{ mb: 4, p: 3, border: '1px solid #ccc', borderRadius: 2 }}>
      <Typography variant="h5" gutterBottom>Your Pantry</Typography>
      
      <Grid container spacing={2} alignItems="center">
        <Grid item xs={12} sm={8}>
          <Autocomplete
            freeSolo
            options={commonIngredients}
            inputValue={inputValue}
            onInputChange={(_, newValue) => setInputValue(newValue)}
            renderInput={(params) => (
              <TextField 
                {...params} 
                label="Add an ingredient"
                fullWidth
                onKeyPress={handleKeyPress}
              />
            )}
          />
        </Grid>
        <Grid item xs={12} sm={4}>
          <Button 
            variant="outlined" 
            color="primary" 
            onClick={handleAdd}
            fullWidth
            sx={{ height: '56px' }}
          >
            Add Ingredient
          </Button>
        </Grid>
      </Grid>
      
      <Box sx={{ mt: 2, display: 'flex', flexWrap: 'wrap', gap: 1 }}>
        {ingredients.map((ingredient, index) => (
          <Chip
            key={index}
            label={ingredient}
            onDelete={() => removeIngredient(ingredient)}
            sx={{ textTransform: 'capitalize' }}
          />
        ))}
      </Box>
    </Box>
  );
};

export default IngredientSelector;
