import React from 'react';
import { 
  Card, 
  CardContent, 
  Typography, 
  Chip, 
  Stack, 
  Box, 
  Divider 
} from '@mui/material';
import { Favorite, AccessTime, LocalDining } from '@mui/icons-material';

const RecipeCard = ({ recipe }) => {
  const getSodiumStatus = (sodium) => {
    if (sodium < 150) return { label: 'Very Low Sodium', color: 'success' };
    if (sodium < 300) return { label: 'Low Sodium', color: 'success' };
    if (sodium < 600) return { label: 'Moderate Sodium', color: 'warning' };
    return { label: 'High Sodium', color: 'error' };
  };

  const sodiumStatus = getSodiumStatus(recipe.sodium);

  return (
    <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <CardContent sx={{ flexGrow: 1 }}>
        <Typography variant="h6" gutterBottom>
          {recipe.name}
        </Typography>
        
        <Stack direction="row" spacing={1} sx={{ mb: 2 }}>
          <Chip 
            label={`${recipe.matchScore}% Match`}
            color="primary"
            size="small"
          />
          <Chip 
            label={sodiumStatus.label}
            color={sodiumStatus.color}
            size="small"
          />
          {recipe.hasDiabetes && recipe.carbs > 30 && (
            <Chip 
              label="High Carb"
              color="warning"
              size="small"
            />
          )}
        </Stack>
        
        <Typography variant="body2" color="text.secondary" gutterBottom>
          <strong>Ingredients:</strong> {recipe.ingredients.map(i => i.name).join(', ')}
        </Typography>
        
        <Divider sx={{ my: 2 }} />
        
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
          <Chip 
            icon={<AccessTime fontSize="small" />}
            label={`${Math.floor(Math.random() * 10) + 15} min`}
            size="small"
          />
          <Chip 
            icon={<LocalDining fontSize="small" />}
            label={`${recipe.calories} cal`}
            size="small"
          />
          <Chip 
            icon={<Favorite fontSize="small" />}
            label={`${recipe.sodium}mg sodium`}
            size="small"
            color={sodiumStatus.color}
          />
        </Box>
        
        <Typography variant="body2" sx={{ mt: 2, fontStyle: 'italic' }}>
          {recipe.instructions.substring(0, 100)}...
        </Typography>
      </CardContent>
    </Card>
  );
};

export default RecipeCard;
