import React from 'react';
import { Box, Typography, TextField, FormControlLabel, Checkbox, Button } from '@mui/material';
import { useRecipeContext } from '../context/RecipeContext';

const HealthProfile = () => {
  const { healthProfile, updateHealthProfile, getRecommendations } = useRecipeContext();

  const handleChange = (e) => {
  const { name, value, type, checked } = e.target;
  
  updateHealthProfile({
    [name]: type === 'checkbox' ? checked : value
  });
};

  return (
    <Box 
      sx={{ 
        mb: 4, 
        p: { xs: 3, md: 4 }, 
        borderRadius: 3,
        backgroundColor: 'background.paper',
        boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)',
      }}
    >
      <Typography 
        variant="h5" 
        gutterBottom
        sx={{ 
          mb: 3,
          fontWeight: 700,
          color: 'text.primary',
        }}
      >
        Health Profile
      </Typography>
      
      <TextField
        fullWidth
        label="Daily Sodium Limit (mg)"
        name="dailySodiumMax"
        type="number"
        value={healthProfile.dailySodiumMax}
        onChange={handleChange}
        margin="normal"
        InputProps={{ inputProps: { min: 500, max: 3000 } }}
        sx={{ mb: 2 }}
      />
      
      <Box sx={{ mb: 3 }}>
        <FormControlLabel
          control={
            <Checkbox
              name="hasDiabetes"
              checked={healthProfile.hasDiabetes}
              onChange={handleChange}
              sx={{ 
                '&.Mui-checked': {
                  color: 'primary.main',
                }
              }}
            />
          }
          label="I have diabetes"
          sx={{ display: 'block', mt: 1 }}
        />
        
        <FormControlLabel
          control={
            <Checkbox
              name="hasRenalDisease"
              checked={healthProfile.hasRenalDisease}
              onChange={handleChange}
              sx={{ 
                '&.Mui-checked': {
                  color: 'primary.main',
                }
              }}
            />
          }
          label="I have kidney disease"
          sx={{ display: 'block', mb: 2 }}
        />
      </Box>
      
      <Button 
        variant="contained" 
        color="primary"
        onClick={getRecommendations}
        fullWidth
        size="large"
        sx={{ 
          mt: 2,
          py: 1.5,
        }}
      >
        Get Recommendations
      </Button>
    </Box>
  );
};

export default HealthProfile;
