import React, { createContext, useState, useContext } from 'react';
import { getRecipes, recommendRecipes } from '../services/api';

const RecipeContext = createContext();

export function RecipeProvider({ children }) {
  const [ingredients, setIngredients] = useState([]);
  const [healthProfile, setHealthProfile] = useState({
    dailySodiumMax: 1500,
    hasDiabetes: false,
    hasRenalDisease: false
  });
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);

  const addIngredient = (ingredient) => {
    if (ingredient && !ingredients.includes(ingredient.toLowerCase())) {
      setIngredients([...ingredients, ingredient.toLowerCase()]);
    }
  };

  const removeIngredient = (ingredient) => {
    setIngredients(ingredients.filter(ing => ing !== ingredient));
  };

  const updateHealthProfile = (newProfile) => {
    setHealthProfile({ ...healthProfile, ...newProfile });
  };

  const getRecommendations = async () => {
    setLoading(true);
    try {
      const data = await recommendRecipes(ingredients, healthProfile);
      setRecommendations(data);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <RecipeContext.Provider
      value={{
        ingredients,
        healthProfile,
        recommendations,
        loading,
        addIngredient,
        removeIngredient,
        updateHealthProfile,
        getRecommendations
      }}
    >
      {children}
    </RecipeContext.Provider>
  );
}

export const useRecipeContext = () => useContext(RecipeContext);
