import React, { useState } from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  Chip, 
  Grid,
  Autocomplete,
  Stack
} from '@mui/material';
import { Kitchen, AddCircle } from '@mui/icons-material';
import { useRecipeContext } from '../context/RecipeContext';

const commonIngredients = [
  'chicken', 'salmon', 'rice', 'pasta', 'tomato', 
  'onion', 'garlic', 'broccoli', 'carrot', 'lettuce',
  'olive oil', 'lemon', 'soy sauce', 'lentils', 'beans',
  'potato', 'spinach', 'egg', 'milk', 'cheese'
];

const IngredientSelector = () => {
  const { ingredients, addIngredient, removeIngredient } = useRecipeContext();
  const [inputValue, setInputValue] = useState('');
  
  const handleAdd = () => {
  if (inputValue.trim()) {
    const normalized = normalizeIngredient(inputValue);
    addIngredient(normalized);
    setInputValue('');
  }
};
  
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleAdd();
    }
  };

const normalizeIngredient = (ing) => {
  return ing.trim().toLowerCase().replace(/s+/g, ' ');
};

  return (
    <Box 
      sx={{ 
        mb: 4, 
        p: { xs: 3, md: 4 }, 
        borderRadius: 3,
        backgroundColor: 'background.paper',
        boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)',
      }}
    >
      <Typography 
        variant="h5" 
        gutterBottom
        sx={{ 
          mb: 3,
          fontWeight: 700,
          color: 'text.primary',
        }}
      >
        Your Pantry
      </Typography>
      
      <Grid container spacing={2} alignItems="center">
        <Grid item xs={12} sm={8}>
          <Autocomplete
            freeSolo
            options={commonIngredients}
            inputValue={inputValue}
            onInputChange={(_, newValue) => setInputValue(newValue)}
            renderInput={(params) => (
              <TextField 
                {...params} 
                label="Add an ingredient"
                fullWidth
                onKeyPress={handleKeyPress}
              />
            )}
          />
        </Grid>
        <Grid item xs={12} sm={4}>
          <Button 
            variant="outlined" 
            color="primary" 
            onClick={handleAdd}
            fullWidth
            sx={{ 
              height: '56px',
              borderWidth: 2,
              '&:hover': {
                borderWidth: 2,
              }
            }}
          >
            Add Ingredient
          </Button>
        </Grid>
      </Grid>
      
      {ingredients.length > 0 && (
        <Box 
          sx={{ 
            mt: 3, 
            display: 'flex', 
            flexWrap: 'wrap', 
            gap: 1.5,
            p: 2,
            borderRadius: 2,
            backgroundColor: 'grey.50',
          }}
        >
          {ingredients.map((ingredient, index) => (
            <Chip
              key={index}
              label={ingredient}
              onDelete={() => removeIngredient(ingredient)}
              sx={{ 
                textTransform: 'capitalize',
                fontWeight: 500,
                '& .MuiChip-deleteIcon': {
                  color: 'text.secondary',
                  '&:hover': {
                    color: 'error.main',
                  }
                }
              }}
            />
          ))}
        </Box>
      )}
    </Box>
  );
};

export default IngredientSelector;
